export default {
  data: function data() {
    return {
      editForm: {
        id: 0,
        uID: 0,
        RID: 0,
        uLoginName: "",
        uRealName: "",
        name: "",
        sex: -1,
        age: 0,
        birth: "",
        desc: "",
        addr: "",
        tdLogo: ""
      },
      token: {
        Authorization: "Bearer "
      },
      ruleForm: {
        max_ver: "",
        min_ver: "",
        enable: ""
      },
      beforeAvatarUpload: function beforeAvatarUpload(file) {
        var isJPG = file.type === "image/jpeg";
        var isLt1M = file.size / 1024 / 1024 < 1; // if (!isJPG) {
        //   this.$message.error('上传头像图片只能是 JPG 格式!')
        // }

        if (!isLt1M) {
          this.$message.error("上传头像图片大小不能超过 1MB!");
        }

        return isLt1M;
      }
    };
  },
  methods: {
    onSubmit: function onSubmit() {
      this.$message({
        message: "失败！该操作无权限",
        type: "error"
      });
    },
    handleAvatarSuccess: function handleAvatarSuccess(res, file) {
      this.editForm.tdLogo = "/" + res.response;
    },
    fileDownload: function fileDownload() {
      var self = this;
      var token = "132465"; // 将token放到头中，设置下载进度、请求参数、返回类型

      axios({
        url: '/images/Down/Bmd?filename=' + self.rulesForm.data.ItemBmdPath,
        method: 'get',
        headers: {
          'FileToken': token
        },
        onDownloadProgress: function onDownloadProgress(p) {// this.precent = Math.floor(100 * (p.loaded / p.total))
        },
        responseType: 'blob'
      }).then(function (data) {
        // 如果后端用encodeURI对文件名进行了编码，前端需用decodeURI进行解码（主要为处理特殊字符）
        var fileName = decodeURI(data.headers.filename); // 由于ie不支持download属性，故需要做兼容判断

        if (navigator.appVersion.toString().indexOf('.NET') > 0) {
          // ie独有的msSaveBlob属性，data.data为Blob文件流
          window.navigator.msSaveBlob(data.data, fileName);
        } else {
          // 以下流程即为文章开始的下载流程
          var url = window.URL.createObjectURL(data.data);
          var link = document.createElement('a');
          link.style.display = 'none';
          link.href = url;
          link.download = fileName;
          document.body.appendChild(link);
          link.click();
          window.URL.revokeObjectURL(link.href);
        }
      });
    }
  },
  mounted: function mounted() {
    var tokenStr = window.localStorage.Token;
    this.token = {
      Authorization: "Bearer " + tokenStr
    };
    var user = JSON.parse(window.localStorage.user);
    this.editForm.uRealName = user ? user.uRealName : "";
  }
};
