import { quillEditor } from "vue-quill-editor"; //调用编辑器
// require styles

import "quill/dist/quill.core.css";
import "quill/dist/quill.snow.css";
import "quill/dist/quill.bubble.css";
import { getBlogDeatil, editBlog } from "../../api/api";
export default {
  data: function data() {
    return {
      id: this.$route.params.id,
      infoForm: {
        btitle: "",
        bsubmitter: "",
        bcategory: "技术博文",
        bcontent: ""
      },
      editorOption: {},
      //表单验证
      rules: {
        btitle: [{
          required: true,
          message: "请输入标题",
          trigger: "blur"
        }],
        bcontent: [{
          required: true,
          message: "请输入详细内容",
          trigger: "blur"
        }]
      }
    };
  },
  computed: {
    editor: function editor() {
      return this.$refs.myQuillEditor.quill;
    }
  },
  mounted: function mounted() {
    var _this = this;

    //初始化
    var para = {
      id: this.id
    };
    getBlogDeatil(para).then(function (res) {
      _this.infoForm = res.data.response;
    });
  },
  methods: {
    onEditorReady: function onEditorReady(editor) {},
    onSubmit: function onSubmit() {
      var _this2 = this;

      //提交
      //this.$refs.infoForm.validate，这是表单验证
      this.$refs.infoForm.validate(function (valid) {
        if (valid) {
          console.log(_this2.infoForm);
          var postPara = _this2.infoForm;
          editBlog(postPara).then(function (res) {
            if (res.data.success) {
              _this2.$notify({
                type: "success",
                message: "修改成功!",
                duration: 3000
              });

              _this2.$router.replace("/Blog/Blogs");
            } else {
              var errorMsg = res.data.msg;

              _this2.$message({
                type: "error",
                message: errorMsg,
                showClose: true
              });
            }
          });
        }
      });
    }
  },
  components: {
    //使用编辑器
    quillEditor: quillEditor
  }
};
