import util from '../../../util/date'
import {testapi, getUserListPage,getRoleListPage , removeUser, batchRemoveUser, editUser, addUser} from '../../api/api';
import { getButtonList } from "../../promissionRouter";
import Toolbar from "../../components/Toolbar";

export default {
    components: { Toolbar },
    data() {
        return {
            filters: {
                name: ''
            },
            users: [],
            roles: [],
            total: 0,
            buttonList: [],
            currentRow: null,
            page: 1,
            listLoading: false,
            sels: [],//列表选中列

            addDialogFormVisible: false,
            editFormVisible: false,//编辑界面是否显示
            editLoading: false,
            editFormRules: {
                uLoginName: [
                    {required: true, message: '请输入登录名', trigger: 'blur'}
                ],
                uRealName: [
                    {required: true, message: '请输入昵称', trigger: 'blur'}
                ],
                birth: [
                    {required: true, message: '请填写生日', trigger: 'blur'}
                ]
            },
            //编辑界面数据
            editForm: {
                id: 0,
                uID: 0,
                RIDs: 0,
                uLoginName: '',
                uRealName: '',
                name: '',
                sex: -1,
                age: 0,
                birth: '',
                addr: ''
            },

            addFormVisible: false,//新增界面是否显示
            addLoading: false,
            addFormRules: {
                uLoginName: [
                    {required: true, message: '请输入登录名', trigger: 'blur'}
                ],
                uRealName: [
                    {required: true, message: '请输入昵称', trigger: 'blur'}
                ],
                uLoginPWD: [
                    {required: true, message: '请输入密码', trigger: 'blur'}
                ],
                birth: [
                    {required: true, message: '请填写生日', trigger: 'blur'}
                ]
            },
            //新增界面数据
            addForm: {
                name: '',
                uLoginName: '',
                uRealName: '',
                uLoginPWD: '',
                sex: -1,
                age: 0,
                birth: '',
                addr: ''
            }

        }
    },
    methods: {
        selectCurrentRow(val) {
        this.currentRow = val;
        },
        callFunction(item) {
        this.filters = {
            name: item.search
        };
        this[item.Func].apply(this, item);
        },
        //性别显示转换
        formatSex: function (row, column) {
            return row.sex == 1 ? '男' : row.sex == 0 ? '女' : '未知';
        },
        formatBirth: function (row, column) {
            return (!row.birth || row.birth == '') ? '' : util.formatDate.format(new Date(row.birth), 'yyyy-MM-dd');
        },
        handleCurrentChange(val) {
            this.page = val;
            this.getUsers();
        },
        //获取用户列表
        getUsers() {
            let para = {
                page: this.page,
                key: this.filters.name
            };
            this.listLoading = true;

            testapi();
            //NProgress.start();
            getUserListPage(para).then((res) => {

                this.total = res.data.response.dataCount;
                this.users = res.data.response.data;
                this.listLoading = false;
                //NProgress.done();
            });
        },
        //删除
        handleDel() {
            let row = this.currentRow;
            if (!row) {
                this.$message({
                message: "请选择要删除的一行数据！",
                type: "error"
                });

                return;
            }
            this.$confirm('确认删除该记录吗?', '提示', {
                type: 'warning'
            }).then(() => {
                this.listLoading = true;
                //NProgress.start();
                let para = {id: row.uID};
                removeUser(para).then((res) => {

                    if (util.isEmt.format(res)) {
                        this.listLoading = false;
                        return;
                    }
                    this.listLoading = false;
                    //NProgress.done();
                    if (res.data.success) {
                        this.$message({
                            message: '删除成功',
                            type: 'success'
                        });

                    } else {
                        this.$message({
                            message: res.data.msg,
                            type: 'error'
                        });
                    }

                    this.getUsers();
                });
            }).catch(() => {

            });
        },
        //显示编辑界面
        handleEdit() {
            let row = this.currentRow;
            if (!row) {
                this.$message({
                message: "请选择要编辑的一行数据！",
                type: "error"
                });

                return;
            }
            this.editFormVisible = true;
            this.editForm = Object.assign({}, row);

            getRoleListPage().then((res) => {
                this.roles = res.data.response.data;
            });

        },
        //显示新增界面
        handleAdd() {
            this.addFormVisible = true;
            this.addForm = {
                uLoginName: '',
                uRealName: '',
                uLoginPWD: '',
                name: '',
                sex: -1,
                age: 0,
                birth: '',
                addr: ''
            };
        },
        //编辑
        editSubmit: function () {
            this.$refs.editForm.validate((valid) => {
                if (valid) {
                    this.$confirm('确认提交吗？', '提示', {}).then(() => {
                        this.editLoading = true;
                        //NProgress.start();
                        let para = Object.assign({}, this.editForm);

                        para.birth = (!para.birth || para.birth == '') ? util.formatDate.format(new Date(), 'yyyy-MM-dd') : util.formatDate.format(new Date(para.birth), 'yyyy-MM-dd');

                        editUser(para).then((res) => {

                            if (util.isEmt.format(res)) {
                                this.editLoading = false;
                                return;
                            }
                            if (res.data.success) {
                                this.editLoading = false;
                                //NProgress.done();
                                this.$message({
                                    message: res.data.msg,
                                    type: 'success'
                                });
                                this.$refs['editForm'].resetFields();
                                this.editFormVisible = false;
                                this.getUsers();
                            } else {
                                this.$message({
                                    message: res.data.msg,
                                    type: 'error'
                                });

                            }
                        });
                    });
                }
            });
        },
        //新增
        addSubmit: function () {
            this.$refs.addForm.validate((valid) => {
                if (valid) {
                    this.$confirm('确认提交吗？', '提示', {}).then(() => {
                        this.addLoading = true;
                        //NProgress.start();
                        let para = Object.assign({}, this.addForm);
                        para.birth = (!para.birth || para.birth == '') ? util.formatDate.format(new Date(), 'yyyy-MM-dd') : util.formatDate.format(new Date(para.birth), 'yyyy-MM-dd');
                        addUser(para).then((res) => {

                            if (util.isEmt.format(res)) {
                                this.addLoading = false;
                                return;
                            }

                            if (res.data.success) {
                                this.addLoading = false;
                                //NProgress.done();
                                this.$message({
                                    message: res.data.msg,
                                    type: 'success'
                                });
                                this.$refs['addForm'].resetFields();
                                this.addFormVisible = false;
                                this.getUsers();
                            }
                            else {
                                this.$message({
                                    message: res.data.msg,
                                    type: 'error'
                                });

                            }

                        });

                    });
                }
            });
        },
        selsChange: function (sels) {
            this.sels = sels;
        },
        //批量删除
        batchRemove: function () {

            // return;

            var ids = this.sels.map(item => item.uID).toString();
            this.$confirm('确认删除选中记录吗？', '提示', {
                type: 'warning'
            }).then(() => {
                this.listLoading = true;
                //NProgress.start();
                let para = {ids: ids};

                batchRemoveUser(para).then((res) => {
                    this.listLoading = false;
                    //NProgress.done();
                    this.$message({
                        message: '该功能未开放',
                        type: 'warning'
                    });
                    console.log(res)
                });
            }).catch(() => {

            });
        }
    },
    mounted() {
        this.getUsers();

        let routers = window.localStorage.router
        ? JSON.parse(window.localStorage.router)
        : [];
        this.buttonList = getButtonList(this.$route.path, routers);
        debugger
    }
}
