import util from '../../../util/date'
import { getUserListPage, getAllRole, removeUser, batchRemoveUser, editUser, addUser } from '../../api/api';
import { getButtonList } from "../../promissionRouter";
import Toolbar from "../../components/ToolbarButton";
import searchForm from "../../components/SearchForm";
import ToolbarExport from "../../components/ToolbarExport";
import { isShowOperatorButtonCommon, isNeedShowOperatorColumn } from '../../../util/common'


export default {
  components: { Toolbar, searchForm, ToolbarExport },//注册子组件
  data () {
    return {
      //导出组件相关
      exportFileName: '用户信息',//要导出的文件名
      currentPageData: [],//当前页面的列表数据
      tHeader: [],//当前页面列表的表头汉字数组，导出用
      filterVal: [],//当前页面列表的表头属性数组，导出用

      //搜索框相关
      commonSearchOptionSet: "模糊",//通用查询的默认配置,"模糊"或"精准" 
      controlStyle: {//设置搜索控件的长度等样式
        width: '300px',
      },
      labelWidth: "90px",//显示Label的宽度
      drawerSize: "550px",//drawner宽度设置
      searchFormInputPlaceholder: '请输入昵称/登录名',//要给子搜索组件传递的值
      searchFormInputAttrs: ["uRealName", "uLoginName"],
      formOptions: [
        {
          label: '昵称',
          prop: 'uRealName',
          element: 'el-input'
        },
        {
          label: '登录名',
          prop: 'uLoginName',
          element: 'el-input'
        },
        {
          label: '角色名',
          prop: 'RoleNames',
          element: 'el-input'
        }
      ],

      isShowOperatorColumn: true,// 是否显示【操作】列,true显示 false不显示
      tableHeight: window.innerHeight - 180, // 控制表格的高度

      filters: {
        name: ''
      },
      users: [],
      roles: [],
      total: 0,
      buttonList: [],
      currentRow: null,
      page: 1,
      pageSize: 10,
      listLoading: false,
      sels: [],//列表选中列

      addDialogFormVisible: false,
      editFormVisible: false,//编辑界面是否显示
      editLoading: false,
      editFormRules: {
        uLoginName: [
          { required: true, message: '请输入登录名', trigger: 'blur' }
        ],
        uRealName: [
          { required: true, message: '请输入昵称', trigger: 'blur' }
        ],
        birth: [
          { required: true, message: '请填写生日', trigger: 'blur' }
        ]
      },
      //编辑界面数据
      editForm: {
        id: 0,
        uID: 0,
        RIDs: 0,
        uLoginName: '',
        uRealName: '',
        name: '',
        sex: -1,
        age: 0,
        birth: '',
        addr: ''
      },

      addFormVisible: false,//新增界面是否显示
      addLoading: false,
      addFormRules: {
        uLoginName: [
          { required: true, message: '请输入登录名', trigger: 'blur' }
        ],
        uRealName: [
          { required: true, message: '请输入昵称', trigger: 'blur' }
        ],
        uLoginPWD: [
          { required: true, message: '请输入密码', trigger: 'blur' }
        ],
        birth: [
          { required: true, message: '请填写生日', trigger: 'blur' }
        ]
      },
      //新增界面数据
      addForm: {
        name: '',
        uLoginName: '',
        uRealName: '',
        uLoginPWD: '',
        sex: -1,
        age: 0,
        birth: '',
        addr: ''
      }

    }
  },
  methods: {
    //是否显示某个操作按钮
    isShowOperatorButton (flag) {
      return isShowOperatorButtonCommon(this.isShowOperatorColumn, flag, this.buttonList)
    },
    // 设置索引
    indexMethod (index) {
      return (this.page - 1) * this.pageSize + 1 + index
    },
    selectCurrentRow (val) {
      this.currentRow = val;
    },
    callFunction (item) {
      this.filters = {
        name: item.search
      };
      this[item.Func].apply(this, item);
    },
    //性别显示转换
    formatSex: function (row, column) {
      return row.sex == 1 ? '男' : row.sex == 0 ? '女' : '未知';
    },
    formatBirth: function (row, column) {
      return (!row.birth || row.birth == '') ? '' : util.formatDate.format(new Date(row.birth), 'yyyy-MM-dd');
    },
    handleCurrentChange (val) {
      this.page = val;
      this.getUsers();
    },
    handleSizeChange (val) {
      this.pageSize = val;
      this.getUsers();
    },
    /**
     * 获取请求参数
     * flag:标记，1代表普通分页查询，2代表不分页，获取全部数据
     */
    getPostParam (flag) {
      let para = Object.assign({}, this.$refs.ChildSearchForm.getFormData());
      para.page = this.page
      para.pageSize = this.pageSize
      if (flag === '2') { // 全部导出
        para.page = 1
        para.pageSize = 10000
      }
      return para
    },
    //获取用户列表
    getUsers (formValue) {
      //this.total = 0
      //this.users = null
      let para = this.getPostParam('1');
      this.listLoading = true;
      getUserListPage(para).then((res) => {
        this.total = res.data.response.dataCount;
        this.users = res.data.response.data;
        this.listLoading = false;
      });
    },
    //获取全部用户列表
    exportAllData () {
      let para = this.getPostParam('2');
      this.listLoading = true;
      getUserListPage(para).then((res) => {
        var allData = res.data.response.data;
        this.$refs.cmToolbarExport.export2Excel(allData);
        this.listLoading = false;
      });
    },
    //删除
    handleDel (selRow) {
      let row = {};
      if (selRow && selRow != undefined) {
        this.currentRow = selRow;
      }
      row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要删除的一行数据！",
          type: "error"
        });

        return;
      }
      this.$confirm('确认删除该记录吗?', '提示', {
        type: 'warning'
      }).then(() => {
        this.listLoading = true;
        //NProgress.start();
        let para = { id: row.uID };
        removeUser(para).then((res) => {

          if (util.isEmt.format(res)) {
            this.listLoading = false;
            return;
          }
          this.listLoading = false;
          //NProgress.done();
          if (res.data.success) {
            this.$message({
              message: '删除成功',
              type: 'success'
            });

          } else {
            this.$message({
              message: res.data.msg,
              type: 'error'
            });
          }

          this.getUsers();
        });
      }).catch(() => {

      });
    },
    //显示编辑界面
    handleEdit (selRow) {
      let row = {};
      if (selRow && selRow != undefined) {
        this.currentRow = selRow;
      }
      row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要编辑的一行数据！",
          type: "error"
        });

        return;
      }
      this.editFormVisible = true;
      this.editForm = Object.assign({}, row);

      getAllRole().then((res) => {
        this.roles = res.data.response;
      });

    },
    //显示新增界面
    handleAdd () {
      this.addFormVisible = true;
      this.addForm = {
        uLoginName: '',
        uRealName: '',
        uLoginPWD: '',
        name: '',
        sex: -1,
        age: 0,
        birth: '',
        addr: ''
      };
    },
    //编辑
    editSubmit: function () {
      this.$refs.editForm.validate((valid) => {
        if (valid) {
          this.$confirm('确认提交吗？', '提示', {}).then(() => {
            this.editLoading = true;
            //NProgress.start();
            let para = Object.assign({}, this.editForm);

            para.birth = (!para.birth || para.birth == '') ? util.formatDate.format(new Date(), 'yyyy-MM-dd') : util.formatDate.format(new Date(para.birth), 'yyyy-MM-dd');

            editUser(para).then((res) => {

              if (util.isEmt.format(res)) {
                this.editLoading = false;
                return;
              }
              if (res.data.success) {
                this.editLoading = false;
                //NProgress.done();
                this.$message({
                  message: res.data.msg,
                  type: 'success'
                });
                this.$refs['editForm'].resetFields();
                this.editFormVisible = false;
                this.getUsers();
              } else {
                this.$message({
                  message: res.data.msg,
                  type: 'error'
                });

              }
            });
          });
        }
      });
    },
    //新增
    addSubmit: function () {
      this.$refs.addForm.validate((valid) => {
        if (valid) {
          this.$confirm('确认提交吗？', '提示', {}).then(() => {
            this.addLoading = true;
            //NProgress.start();
            let para = Object.assign({}, this.addForm);
            para.birth = (!para.birth || para.birth == '') ? util.formatDate.format(new Date(), 'yyyy-MM-dd') : util.formatDate.format(new Date(para.birth), 'yyyy-MM-dd');
            addUser(para).then((res) => {

              if (util.isEmt.format(res)) {
                this.addLoading = false;
                return;
              }

              if (res.data.success) {
                this.addLoading = false;
                //NProgress.done();
                this.$message({
                  message: res.data.msg,
                  type: 'success'
                });
                this.$refs['addForm'].resetFields();
                this.addFormVisible = false;
                this.getUsers();
              }
              else {
                this.$message({
                  message: res.data.msg,
                  type: 'error'
                });

              }

            });

          });
        }
      });
    },
    selsChange: function (sels) {
      this.sels = sels;
    },
    //批量删除
    batchRemove: function () {

      // return;

      var ids = this.sels.map(item => item.uID).toString();
      this.$confirm('确认删除选中记录吗？', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        this.listLoading = true;
        //NProgress.start();
        let para = { ids: ids };

        batchRemoveUser(para).then((res) => {
          this.listLoading = false;
          //NProgress.done();
          this.$message({
            message: '该功能未开放',
            type: 'warning'
          });
          console.log(res)
        });
      }).catch(() => {

      });
    },
    //导出
    handleExport () {
      this.tHeader = ['昵称', '登录名'] // 表头
      this.filterVal = ['uRealName', 'uLoginName'] // 字段

      this.currentPageData = this.users
      this.$refs.cmToolbarExport.showPrintPage()
    },
  },
  mounted () {
    this.getUsers();

    let routers = window.localStorage.router
      ? JSON.parse(window.localStorage.router)
      : [];
    this.buttonList = getButtonList(this.$route.path, routers);
    let isShow = isNeedShowOperatorColumn(this.buttonList);//控制是否要显示【操作】列
    this.isShowOperatorColumn = isShow;
  }
}
