import util from '../../../util/date'
import { getCodeItemListPage, removeCodeItem, editCodeItem, addCodeItem, getAllCodeSets } from '../../api/api';
import { getButtonList } from "../../promissionRouter";
import Toolbar from "../../components/ToolbarButton";
import SearchForm from "../../components/SearchForm";
import ToolbarExport from "../../components/ToolbarExport";
import { formatDate, setformOptionsNewValue } from '../../../util/tools'
import { isShowOperatorButtonCommon, isNeedShowOperatorColumn } from '../../../util/common'

export default {
  components: { Toolbar, SearchForm, ToolbarExport },//注册子组件
  data () {
    return {
      allCodeSets: [],

      //导出组件相关
      exportFileName: '码表集信息',//要导出的文件名
      currentPageData: [],//当前页面的列表数据
      tHeader: ['代码编码', '代码名称', '说明'],//当前页面列表的表头汉字数组，导出用
      filterVal: ['code', 'name', 'note'],//当前页面列表的表头属性数组，导出用

      //搜索框相关
      commonSearchOptionSet: "精准",//通用查询的默认配置,"模糊"或"精准"
      controlStyle: {//设置搜索控件的长度等样式
        width: '300px',
      },
      labelWidth: "90px",//显示Label的宽度
      drawerSize: "550px",//drawner宽度设置
      searchFormInputPlaceholder: '请输入代码编码/名称',//要给子搜索组件传递的值
      searchFormInputAttrs: ['code', 'name'],//要给子搜索组件传递的属性名
      formOptions: [
        {
          label: '代码集编码',
          prop: 'setCode',
          element: 'el-select',
          options: [] //先默认为空，再通过请求获取数据
        },
        {
          label: '代码编码',
          prop: 'code',
          element: 'el-input'
        },
        {
          label: '代码名称',
          prop: 'name',
          element: 'el-input'
        },
        {
          label: '说明',
          prop: 'note',
          element: 'el-input'
        }
      ],

      isShowOperatorColumn: true,// 是否显示【操作】列,true显示 false不显示
      tableHeight: window.innerHeight - 180, // 控制表格的高度

      isAdd: false, // 是否是新增
      dialogTitle: '',

      filters: {
        name: ''
      },
      CodeItems: [],
      roles: [],
      total: 0,
      buttonList: [],
      currentRow: null,
      page: 1,
      pageSize: 10,
      listLoading: false,
      sels: [],//列表选中列


      addFormVisible: false,//新增界面是否显示
      addLoading: false,
      //新增界面数据
      addForm: {
        id: 0,
        name: '',
        code: '',
        note: '',
        delFlag: false
      }

    }
  },
  methods: {
    //是否显示某个操作按钮
    isShowOperatorButton (flag) {
      return isShowOperatorButtonCommon(this.isShowOperatorColumn, flag, this.buttonList)
    },
    // 获取所有代码集
    getAllCodeSet: function () {
      getAllCodeSets().then(res => {
        if (res.data.success) {
          this.allCodeSets = res.data.response

          //给搜索框中的select控件赋值
          let propValue = "setCode";
          this.formOptions = setformOptionsNewValue(this.formOptions, propValue, this.allCodeSets);
          this.$refs.ChildSearchForm.setNewFormOptions(this.formOptions)
        }
        else {
          this.$notify.error({
            title: '错误',
            message: res.msg
          })
        }
      }).catch(err => {
        console.log(err)
      })
    },
    // 设置索引
    indexMethod (index) {
      return (this.page - 1) * this.pageSize + 1 + index
    },
    // 时间格式化
    formatterDateTime (row, column, cellValue, index) {
      if (cellValue === null || cellValue === '') {
        return ''
      }
      var NewDtime = new Date(Date.parse(cellValue))
      return formatDate(NewDtime, 'yyyy-MM-dd hh:mm:ss')
    },
    selectCurrentRow (val) {
      this.currentRow = val;
    },
    callFunction (item) {
      this.filters = {
        name: item.search
      };
      this[item.Func].apply(this, item);
    },
    handleCurrentChange (val) {
      this.page = val;
      this.getCodeItems();
    },
    handleSizeChange (val) {
      this.pageSize = val;
      this.getCodeItems();
    },
    /**
     * 获取请求参数
     * flag:标记，1代表普通分页查询，2代表不分页，获取全部数据
     */
    getPostParam (flag) {
      let para = Object.assign({}, this.$refs.ChildSearchForm.getFormData());
      para.page = this.page
      para.pageSize = this.pageSize
      if (flag === '2') { // 全部导出
        para.page = 1
        para.pageSize = 10000
      }
      return para
    },
    //获取列表
    getCodeItems (formValue) {
      //this.total = 0
      //this.CodeItems = null
      let para = this.getPostParam('1');
      this.listLoading = true;
      getCodeItemListPage(para).then((res) => {
        if (res.data.success) {
          this.total = res.data.response.dataCount;
          this.CodeItems = res.data.response.data;
        } else {
          this.$message({
            message: res.data.msg,
            type: 'error'
          });
        }
        this.listLoading = false;
      });
    },
    //获取全部列表
    exportAllData () {
      let para = this.getPostParam('2');
      this.listLoading = true;
      getCodeItemListPage(para).then((res) => {
        var allData = res.data.response.data;
        this.$refs.cmToolbarExport.export2Excel(allData);
        this.listLoading = false;
      });
    },
    //删除 
    handleDel (selRow) {
      let row = {};
      if (selRow && selRow != undefined) {
        this.currentRow = selRow;
      }
      row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要删除的一行数据！",
          type: "error"
        });

        return;
      }
      this.$confirm('确认删除该记录吗?', '提示', {
        type: 'warning'
      }).then(() => {
        this.listLoading = true;
        //NProgress.start();
        let para = { id: row.Id };
        debugger
        removeCodeItem(para).then((res) => {

          if (util.isEmt.format(res)) {
            this.listLoading = false;
            return;
          }
          this.listLoading = false;
          //NProgress.done();
          if (res.data.success) {
            this.$message({
              message: '删除成功',
              type: 'success'
            });

          } else {
            this.$message({
              message: res.data.msg,
              type: 'error'
            });
          }

          this.getCodeItems();
        });
      }).catch(() => {

      });
    },
    //显示编辑界面
    handleEdit (selRow) {
      let row = {};
      if (selRow && selRow != undefined) {
        this.currentRow = selRow;
      }
      row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要编辑的一行数据！",
          type: "error"
        });

        return;
      }
      this.isAdd = false
      this.dialogTitle = '编辑'
      this.addFormVisible = true;
      this.addForm = {
      };
      debugger
      this.addForm = Object.assign({}, row);
    },
    //显示新增界面
    handleAdd () {
      this.isAdd = true
      this.dialogTitle = '新增'
      this.addFormVisible = true;
      this.addForm = {
      };
    },
    // 更新数据
    SaveData (formName) {
      this.submitDisabled = true

      this.$refs.addForm.validate((valid) => {
        if (valid) {
          this.$confirm('确认提交吗？', '提示', {}).then(() => {
            this.addLoading = true;
            let para = Object.assign({}, this.addForm);
            if (this.isAdd) {//新增
              addCodeItem(para).then((res) => {

                if (util.isEmt.format(res)) {
                  this.addLoading = false;
                  return;
                }

                if (res.data.success) {
                  this.addLoading = false;
                  //NProgress.done();
                  this.$message({
                    message: res.data.msg,
                    type: 'success'
                  });
                  this.$refs['addForm'].resetFields();
                  this.addFormVisible = false;
                  this.getCodeItems();
                }
                else {
                  this.addLoading = false;
                  this.$message({
                    message: res.data.msg,
                    type: 'error'
                  });
                }

              });
            } else {//编辑
              editCodeItem(para).then((res) => {

                if (util.isEmt.format(res)) {
                  this.addLoading = false;
                  return;
                }
                if (res.data.success) {
                  this.addLoading = false;
                  //NProgress.done();
                  this.$message({
                    message: res.data.msg,
                    type: 'success'
                  });
                  this.$refs['addForm'].resetFields();
                  this.addFormVisible = false;
                  this.getCodeItems();
                }
                else {
                  this.addLoading = false;
                  this.$message({
                    message: res.data.msg,
                    type: 'error'
                  });
                }
              });
            }
          });
        }
      });
    },
    selsChange: function (sels) {
      this.sels = sels;
    },
    //导出
    handleExport () {
      this.currentPageData = this.CodeItems
      this.$refs.cmToolbarExport.showPrintPage()
    },
  },
  mounted () {
    this.getAllCodeSet()
    this.getCodeItems()

    let routers = window.localStorage.router
      ? JSON.parse(window.localStorage.router)
      : [];
    this.buttonList = getButtonList(this.$route.path, routers);
    let isShow = isNeedShowOperatorColumn(this.buttonList);//控制是否要显示【操作】列
    this.isShowOperatorColumn = isShow;
  }
}
