import util from '../../../util/date'
import { getStockInfoListPage, removeStockInfo, editStockInfo, addStockInfo } from '../../api/api';
import { getButtonList } from "../../promissionRouter";
import Toolbar from "../../components/ToolbarButton";
import SearchForm from "../../components/SearchForm";
import ToolbarExport from "../../components/ToolbarExport";
import { formatDate } from '../../../util/tools'


export default {
  components: { Toolbar, SearchForm, ToolbarExport },//注册子组件 
  data () {
    return {
      //导出组件相关 
      exportFileName: '库存信息信息',//要导出的文件名 
      currentPageData: [],//当前页面的列表数据 
      tHeader: ['', '', '', '', '', '', '操作说明', '创建者ID', '创建者', '修改ID', '修改者', '修改时间'],//当前页面列表的表头汉字数组，导出用 
      filterVal: ['SKU', 'CargoCode', 'CargoName', 'CargoNum', 'ValidityDate', 'InStockDate', 'OperationRemark', 'CreateId', 'CreateBy', 'ModifyId', 'ModifyBy', 'ModifyTime'],//当前页面列表的表头属性数组，导出用 

      //搜索框相关 
      commonSearchOptionSet: "模糊",//通用查询的默认配置,"模糊"或"精准" 
      controlStyle: {//设置搜索控件的长度等样式 
        width: '300px',
      },
      labelWidth: "90px",//显示Label的宽度 
      drawerSize: "450px",//drawner宽度设置 
      searchFormInputPlaceholder: '请输入//////操作说明/创建者ID/创建者/修改ID/修改者/修改时间',//要给子搜索组件传递的值 
      searchFormInputAttrs: ['SKU', 'CargoCode', 'CargoName', 'CargoNum', 'ValidityDate', 'InStockDate', 'OperationRemark', 'CreateId', 'CreateBy', 'ModifyId', 'ModifyBy', 'ModifyTime'],//要给子搜索组件传递的属性名 
      formOptions: [
        {
          label: '',
          prop: 'SKU',
          element: 'el-input',
        },
        {
          label: '',
          prop: 'CargoCode',
          element: 'el-input',
        },
        {
          label: '',
          prop: 'CargoName',
          element: 'el-input',
        },
        {
          label: '',
          prop: 'CargoNum',
          element: 'el-input',
        },
        {
          label: '',
          prop: 'ValidityDate',
          type: 'datetimerange',
          element: 'el-date-picker',
        },
        {
          label: '',
          prop: 'InStockDate',
          type: 'datetimerange',
          element: 'el-date-picker',
        },
        {
          label: '操作说明',
          prop: 'OperationRemark',
          element: 'el-input',
        },
        {
          label: '创建者ID',
          prop: 'CreateId',
          element: 'el-input',
        },
        {
          label: '创建者',
          prop: 'CreateBy',
          element: 'el-input',
        },
        {
          label: '修改ID',
          prop: 'ModifyId',
          element: 'el-input',
        },
        {
          label: '修改者',
          prop: 'ModifyBy',
          element: 'el-input',
        },
        {
          label: '修改时间',
          prop: 'ModifyTime',
          type: 'datetimerange',
          element: 'el-date-picker',
        },
      ],


      tableHeight: window.innerHeight - 230, // 500, // 这个值不能设置小了，设置小了，界面就不对了 

      isAdd: false, // 是否是新增 
      dialogTitle: '',

      filters: {
        name: ''
      },
      StockInfos: [],
      roles: [],
      total: 0,
      buttonList: [],
      currentRow: null,
      page: 1,
      pageSize: 10,
      listLoading: false,
      sels: [],//列表选中列 

      addFormVisible: false,//新增界面是否显示 
      addLoading: false,
      //新增界面数据 
      addForm: {

      }

    }
  },
  methods: {
    // 设置索引 
    indexMethod (index) {
      return (this.page - 1) * this.pageSize + 1 + index
    },
    // 时间格式化 
    formatterDateTime (row, column, cellValue, index) {
      if (cellValue === null || cellValue === '') {
        return ''
      }
      var NewDtime = new Date(Date.parse(cellValue))
      return formatDate(NewDtime, 'yyyy-MM-dd hh:mm:ss')
    },
    selectCurrentRow (val) {
      this.currentRow = val;
    },
    callFunction (item) {
      this.filters = {
        name: item.search
      };
      this[item.Func].apply(this, item);
    },
    handleCurrentChange (val) {
      this.page = val;
      this.getStockInfos();
    },
    handleSizeChange (val) {
      this.pageSize = val;
      this.getStockInfos();
    },
    /** 
     * 获取请求参数 
     * flag:标记，1代表普通分页查询，2代表不分页，获取全部数据 
     */
    getPostParam (flag) {
      let para = Object.assign({}, this.$refs.ChildSearchForm.getFormData());
      para.page = this.page
      para.pageSize = this.pageSize
      if (flag === '2') { // 全部导出 
        para.page = 1
        para.pageSize = 10000
      }
      return para
    },
    //获取列表 
    getStockInfos (formValue) {
      //this.total = 0 
      //this.StockInfos = null 
      let para = this.getPostParam('1');
      this.listLoading = true;
      getStockInfoListPage(para).then((res) => {
        if (res.data.success) {
          this.total = res.data.response.dataCount;
          this.StockInfos = res.data.response.data;
        } else {
          this.$message({
            message: res.data.msg,
            type: 'error'
          });
        }
        this.listLoading = false;
      });
    },
    //获取全部列表 
    exportAllData () {
      let para = this.getPostParam('2');
      this.listLoading = true;
      getStockInfoListPage(para).then((res) => {
        var allData = res.data.response.data;
        this.$refs.cmToolbarExport.export2Excel(allData);
        this.listLoading = false;
      });
    },
    //删除 
    handleDel () {
      let row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要删除的一行数据！",
          type: "error"
        });
        return;
      }
      this.$confirm('确认删除该记录吗?', '提示', {
        type: 'warning'
      }).then(() => {
        this.listLoading = true;
        let para = { id: row.Id };
        removeStockInfo(para).then((res) => {

          if (util.isEmt.format(res)) {
            this.listLoading = false;
            return;
          }
          this.listLoading = false;
          //NProgress.done(); 
          if (res.data.success) {
            this.$message({
              message: '删除成功',
              type: 'success'
            });

          } else {
            this.$message({
              message: res.data.msg,
              type: 'error'
            });
          }

          this.getStockInfos();
        });
      }).catch(() => {

      });
    },
    //显示编辑界面 
    handleEdit () {
      let row = this.currentRow;
      if (!row) {
        this.$message({
          message: "请选择要编辑的一行数据！",
          type: "error"
        });

        return;
      }
      this.isAdd = false
      this.dialogTitle = '编辑'
      this.addFormVisible = true;
      this.addForm = {
      };
      debugger
      this.addForm = Object.assign({}, row);
    },
    //显示新增界面 
    handleAdd () {
      this.isAdd = true
      this.dialogTitle = '新增'
      this.addFormVisible = true;
      this.addForm = {
      };
    },
    // 更新数据 
    SaveData (formName) {
      this.submitDisabled = true

      this.$refs.addForm.validate((valid) => {
        if (valid) {
          this.$confirm('确认提交吗？', '提示', {}).then(() => {
            this.addLoading = true;
            let para = Object.assign({}, this.addForm);
            if (this.isAdd) {//新增 
              addStockInfo(para).then((res) => {

                if (util.isEmt.format(res)) {
                  this.addLoading = false;
                  return;
                }

                if (res.data.success) {
                  this.addLoading = false;
                  //NProgress.done(); 
                  this.$message({
                    message: res.data.msg,
                    type: 'success'
                  });
                  this.$refs['addForm'].resetFields();
                  this.addFormVisible = false;
                  this.getStockInfos();
                }
                else {
                  this.addLoading = false;
                  this.$message({
                    message: res.data.msg,
                    type: 'error'
                  });
                }

              });
            } else {//编辑 
              editStockInfo(para).then((res) => {

                if (util.isEmt.format(res)) {
                  this.addLoading = false;
                  return;
                }
                if (res.data.success) {
                  this.addLoading = false;
                  //NProgress.done(); 
                  this.$message({
                    message: res.data.msg,
                    type: 'success'
                  });
                  this.$refs['addForm'].resetFields();
                  this.addFormVisible = false;
                  this.getStockInfos();
                }
                else {
                  this.addLoading = false;
                  this.$message({
                    message: res.data.msg,
                    type: 'error'
                  });
                }
              });
            }
          });
        }
      });
    },
    selsChange: function (sels) {
      this.sels = sels;
    },
    //导出 
    handleExport () {
      this.currentPageData = this.StockInfos
      this.$refs.cmToolbarExport.showPrintPage()
    },
  },
  mounted () {
    this.getStockInfos();

    let routers = window.localStorage.router
      ? JSON.parse(window.localStorage.router)
      : [];
    this.buttonList = getButtonList(this.$route.path, routers);

  }
}
